
public class ExampleArrays {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int a[]=new int [10];
		
        a[0]=1;
        a[1]=5;
        a[2]=4;
        a[3]=7;
        a[4]=3;
        for (int i=0;i<a.length;i++) {
        	
        	System.out.println(a[i]);
        	
        }
        int b[]= {2,3,4};
        for (int i=0;i<b.length;i++) {
        	System.out.println(b[i]);
        }
        
	}

}
